<?phpnamespace frontend\controllers;use yii\data\Pagination;use frontend\models\MaktabNews;use frontend\models\Oquvchi;use frontend\models\Category;use frontend\models\Qonunlar;class NewsController extends \yii\web\Controller{public $layout ="homepage";const COUNT_POSTS_IN_PAGE = 1; //количество постов на странице вывода всех постов    public function actionIndex()    {        $data = MaktabNews::getAll();        $news = new MaktabNews();        $popular = MaktabNews::getPopular();        $categories = Category::getAll();        return $this->render('index', [                    'articles'=> $data['articles'],                     'pagination'=> $data['pagination'],                     'popular'=> $popular,                    'categories'=>$categories,                    'news'=>$news        ]);    }                 public function actionView($id){         $articles = MaktabNews::findOne($id);         $popular = MaktabNews::getPopular();         $categories = Category::getAll();                      return $this->render('view',[            'articles' => $articles,            'popular'=> $popular,            'categories'=>$categories    ]);    }   public function actionCategory($id)     {         $data = Category::getByCategory($id);              $popular = MaktabNews::getPopular();        $categories = Category::getAll();                 return $this->render('category',[        'articles'=>$data['articles'],         'pagination'=> $data['pagination'],         'popular'=> $popular,        'categories'=>$categories    ]);            }          public function actionSetCaegory($id)     {        $article = $this->findModel($id);        $selectedCategoty = $article->category->id;        $caegoris = ArrayHelper::map(Category::find()->all(),'id','title');        if (Yii::$app->request->isPost) {            {                $category = Yii::$app->request->post('category');                if (true) {                    return $this->redirect(['wiew','id'=>$article->id]);                }            }            return $this->render('category',[              'article'=>$article,                 'selectedCategoty'=>$selectedCategoty,               'caegoris'=>$caegoris            ]);        }     }    public function actionBlok()         {          $qonun = Qonunlar::getAll();         return $this->render('qonun',[        'qonun' => $qonun                    ]);    }    }